import React, { Component } from "react"
import logo from './logo.svg';
import axios from 'axios';
import './App.css';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      apiResponse: "",
      complexity: null,
      scrutiny: null,
      meanGrid: null,
      resultingImage: null
    };
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  callAPI() {
      fetch("http://localhost:9000/testAPI")
          .then(res => res.text())
          .then(res => this.setState({ apiResponse: res }));
  }

  componentDidMount() {
      this.callAPI();
  }

  handleSubmit(event) {
    console.log(this.state.complexity);
    console.log(this.state.scrutiny);
    console.log(this.state.meanGrid);
    const data = new FormData();
    data.append('file', this.state.complexity);
    data.append('file', this.state.scrutiny);
    data.append('file', this.state.meanGrid);
    axios.post("http://localhost:9000/testAPI", data)
      .then(res => {
        console.log("res");
        console.log(res);
        this.setState({
          resultingImage: res.data
        });
      })
    event.preventDefault();
  }

  render() {
    return (
      <div className="App">
      <header className="App-header">
        {this.state.resultingImage
        ?
        <img src={`data:image/png;base64,${this.state.resultingImage}`} />
        :
        <img src={logo} className="App-logo" alt="logo" />}
        <p>
          {this.state.apiResponse}
        </p>
      </header>
      <form onSubmit={this.handleSubmit}>
        <label>
          Complexity:
          <input type="file" onChange={(e) => this.setState({
            complexity: e.target.files[0]
          })} />
        </label>
        <label>
          Scrutiny:
          <input type="file" onChange={(e) => this.setState({
            scrutiny: e.target.files[0]
          })} />
        </label>
        <label>
          Mean grid:
          <input type="file" onChange={(e) => this.setState({
            meanGrid: e.target.files[0]
          })} />
        </label>
        <input type="submit" value="Отправить" />
      </form>
      </div>
    )
  }
}

export default App;
