import React, { Component } from "react"
import axios from 'axios';
import './App.css';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      complexity: null,
      scrutiny: null,
      meanGrid: null,
      resultingImage: null,
      loading: false
    };
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  /*callAPI() {
      fetch("http://localhost:9000/testAPI")
          .then(res => res.text())
          .then(res => this.setState({ apiResponse: res }));
  }

  componentDidMount() {
      this.callAPI();
  }*/

  handleSubmit(event) {
    console.log(this.state.complexity);
    console.log(this.state.scrutiny);
    console.log(this.state.meanGrid);
    const data = new FormData();
    data.append('file', this.state.complexity);
    data.append('file', this.state.scrutiny);
    data.append('file', this.state.meanGrid);
    this.setState({
      loading: true
    })
    axios.post("http://localhost:9000/testAPI", data)
      .then(res => {
        console.log("res");
        console.log(res);
        this.setState({
          loading: false,
          resultingImage: res.data
        });
      })
    event.preventDefault();
  }

  render() {
    return (
      <div className="App">
      <div>
        {this.state.resultingImage
        ?
        <img src={`data:image/png;base64,${this.state.resultingImage}`} />
        :
        this.state.loading
        ?
        <div><h1>Loading...</h1></div>
        :
        <div><h1>Please load wells complexity, scrutiny and mean grid</h1></div>
        }
      </div>
      <form onSubmit={this.handleSubmit}>
        <label>
          Complexity:
          <input type="file" onChange={(e) => this.setState({
            complexity: e.target.files[0]
          })} />
        </label>
        <br></br>
        <label>
          Scrutiny:
          <input type="file" onChange={(e) => this.setState({
            scrutiny: e.target.files[0]
          })} />
        </label>
        <br></br>
        <label>
          Mean grid:
          <input type="file" onChange={(e) => this.setState({
            meanGrid: e.target.files[0]
          })} />
        </label>
        <br></br>
        <input type="submit" value="Submit" />
      </form>
      </div>
    )
  }
}

export default App;
